import React, { useState, useEffect } from "react";
import Stock from "./Stock";
import Prediction from "./Prediction";

const PassText = ({ someText }) => {
  const [latestSymbol, setLatestSymbol] = useState(null);
  const [prediction, setPrediction] = useState(null);

  useEffect(() => {
    if (latestSymbol) {
      const predictNum = Math.floor(Math.random() * 2);
      setPrediction(predictNum === 1 ? "up" : "down");
    }
  }, [latestSymbol]);

  const handleStockDataChange = (newData) => {
    setLatestSymbol(newData.symbol);
  };

  return (
    <div>
      {someText.symbol && (
        <>
          <h1>{someText.symbol}</h1>
          <Stock sign={someText.symbol} setStockData={handleStockDataChange} />
          {prediction !== null && (
            <div>
              The prediction is that this stock goes{" "}
              <span style={{ whiteSpace: "nowrap" }}>{prediction}</span> in
              price.
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default PassText;
