import React, { Component } from "react";
import Plot from "react-plotly.js";
import Prediction from "./Prediction";

class Stock extends Component {
  constructor(props) {
    super(props);
    this.state = {
      stockChartXValues: [],
      stockChartYValues: [],
      prediction: null,
    };
  }

  componentDidMount() {
    this.fetchStock(this.props.sign);
  }

  componentDidUpdate(prevProps) {
    if (this.props.sign !== prevProps.sign) {
      this.fetchStock(this.props.sign);
    }
  }

  fetchStock(StockSymbol) {
    const API_KEY = "HGJWFG4N8AQ66ICD";

    let API_Call = `https://www.alphavantage.co/query?function=TIME_SERIES_DAILY_ADJUSTED&symbol=${StockSymbol}&outputsize=compact&apikey=${API_KEY}`;

    let stockChartXValuesFunction = [];
    let stockChartYValuesFunction = [];

    fetch(API_Call)
      .then(function(response) {
        return response.json();
      })
      .then(
        function(data) {
          console.log(data);

          for (var key in data["Time Series (Daily)"]) {
            stockChartXValuesFunction.push(key);
            stockChartYValuesFunction.push(
              data["Time Series (Daily)"][key]["1. open"]
            );
          }

          this.setState({
            stockChartXValues: stockChartXValuesFunction,
            stockChartYValues: stockChartYValuesFunction,
          });
        }.bind(this)
      );
  }

  handlePrediction = (prediction) => {
    this.setState({
      prediction: prediction,
    });
  };

  render() {
    return (
      <div>
        <Plot
          data={[
            {
              x: this.state.stockChartXValues,
              y: this.state.stockChartYValues,
              type: "scatter",
              mode: "lines+markers",
              marker: { color: "red" },
            },
          ]}
          layout={{
            width: 720,
            height: 440,
            title: `${this.props.sign} Chart`,
          }}
        />
        {this.props.sign && this.state.prediction !== null && (
          <div>
            The prediction is that this stock goes{" "}
            <span style={{ whiteSpace: "nowrap" }}>
              <Prediction pred={this.state.prediction} />
            </span>{" "}
            in price.
          </div>
        )}
        <br />
        <br />
        <SearchBar
          sign={this.props.sign}
          handlePrediction={this.handlePrediction}
        />
      </div>
    );
  }
}

export default Stock;
