// import React from "react";
import React, { useState } from "react";

class SearchBar extends React.Component {}
function Bar({ placeholder }) {
  const [data, setData] = useState("AMZN");
  const [print, setPrint] = useState(false);

  function getData(val) {
    setData(val.target.value);
    console.warn(val.target.value);
  }

  return (
    <div>
      <form id="search-form" class="wholebar" onClick={() => setPrint(true)}>
        <div className="search">
          <div className="searchInputs">
            <input
              type="text"
              placeholder={placeholder}
              onChange={getData}
              name={getData}
            />
            <div className="searchIcon"></div>
          </div>
          <div className="dataResult"></div>
          {/* GRAB THE DATA AND SEND IT TO THE STOCK.js */}
          {print ? <h2>{data}</h2> : null}
        </div>
        {/* <passText someText="d"></passText> */}
      </form>
    </div>
  );
}

export default SearchBar;
