import React, { useState } from "react";
import Plot from "react-plotly.js";
import SearchBar from "./SearchBar";

class Stock extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      stockChartXValues: [],
      stockChartYValues: [],
    };
    // this.symb = "AMZN";
    this.symb = SearchBar.data;
  }

  componentDidMount() {
    this.fetchStock();
  }

  // eatData(StockSymbol) {
  //   return StockSymbol;
  // }

  fetchStock() {
    const pointerToThis = this;
    console.log(pointerToThis);
    const API_KEY = "HGJWFG4N8AQ66ICD";

    // const passText = ({ someText }) => {
    //     return <div className="SearchBar">{someText}</div>;
    //   };
    let StockSymbol = pointerToThis.symb;
    // let StockSymbol = "AAPL";
    let API_Call = `https://www.alphavantage.co/query?function=TIME_SERIES_DAILY_ADJUSTED&symbol=${StockSymbol}&outputsize=compact&apikey=${API_KEY}`;

    let stockChartXValuesFunction = [];
    let stockChartYValuesFunction = [];

    fetch(API_Call)
      .then(function(response) {
        return response.json();
      })
      .then(function(data) {
        console.log(data);

        for (var key in data["Time Series (Daily)"]) {
          stockChartXValuesFunction.push(key);
          stockChartYValuesFunction.push(
            data["Time Series (Daily)"][key]["1. open"]
          );
        }

        // console.log(stockChartXValuesFunction);
        pointerToThis.setState({
          stockChartXValues: stockChartXValuesFunction,
          stockChartYValues: stockChartYValuesFunction,
        });
      });
  }

  render() {
    return (
      <div>
        {/* <form id="search-form" class="wholebar">
          <p class="searchtext">Please enter in a stock symbol</p>
          <div class="searchbar">
            <div class="search">
              <input type="text" name="search" class="round" />
            </div>
          </div>
        </form> */}
        <Plot
          data={[
            {
              x: this.state.stockChartXValues,
              y: this.state.stockChartYValues,
              type: "scatter",
              mode: "lines+markers",
              marker: { color: "red" },
            },
          ]}
          layout={{
            width: 720,
            height: 440,
            title: `A chart of ${SearchBar}`,
          }}
        />
        <p>The prediction is that this stock goes up in price.</p>
      </div>
    );
  }
}

export default Stock;
